bind.calculate:
if ( /assessment/group_cough/has_cough ='yes', 
 concat (
 'Cough', 
 if( string-length( /assessment/group_cough/cough_duration ) != 0,
 concat (
 ' for ', 
 jr:choice-name( /assessment/group_cough/cough_duration , ' /assessment/group_cough/cough_duration ')
 ),
 ''
 )
 ),
 ''
)

if ( /assessment/group_diarrhoea/has_diarrhoea ='yes', 
 concat(
 'Diarrhoea', 
 if( string-length( /assessment/group_diarrhoea/diarrhoea_duration ) != 0,
 concat (
 ' for ', 
 jr:choice-name( /assessment/group_diarrhoea/diarrhoea_duration , ' /assessment/group_diarrhoea/diarrhoea_duration ')
 ),
 ''
 ),
 if ( /assessment/group_diarrhoea/blood_in_stool ='yes', ' with blood', '')
 ),
 ''
)

if ( /assessment/group_fever/has_fever ='yes' or  /assessment/group_fever/patient_temperature  > 37.5, 
 concat (
 if( /assessment/group_fever/patient_temperature  >= 40, 
 'Severe fever',
 'Fever'
 ), 
 if( string-length( /assessment/group_fever/fever_duration ) != 0,
 concat (
 ' for ', 
 jr:choice-name( /assessment/group_fever/fever_duration , ' /assessment/group_fever/fever_duration ')
 ),
 ''
 )
 ),
 ''
)

if(( /assessment/group_breathing/breath_count >=60) and ( /assessment/patient_age_in_days <60), "true", 
 if(( /assessment/group_breathing/breath_count >=50) and ( /assessment/patient_age_in_days >=60) and ( /assessment/patient_age_in_days <365), "true",
 if(( /assessment/group_breathing/breath_count >=40) and ( /assessment/patient_age_in_days >=365) and ( /assessment/patient_age_in_days <(5*365.25)), "true",
 if(( /assessment/group_breathing/breath_count >=30) and ( /assessment/patient_age_in_days >=(5*365.25)), "true", "false" ))))
